package comp3350.grs.presentation;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.GridLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.os.Bundle;

import java.util.ArrayList;

import comp3350.grs.R;
import comp3350.grs.objects.Game;

public class BrowseGames extends AppCompatActivity {

    private RecyclerView gamesRecView ;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_browse_games);

        // find the RecyclerView for each Game object.
        gamesRecView = findViewById(R.id.gamesRecView) ;

        // Arraylist of Games (mainly for GamesRecViewAdapter class)
        ArrayList<Game> games = new ArrayList<>() ;

        // Populate the arraylist of games.
        games.add(new Game("Fifa 21"," "," ",  49, "data:image/jpeg;base64,")) ;
        games.add(new Game("Dota 2", "", "",45,"data:image/jpeg;base64,")) ;
        games.add(new Game("Mario Kart Tour","$66","$66", 66, "https://cdn.vox-cdn.com/thumbor/Xm1C9TWbMK55zAnuR09oD6XMyRI=/0x0:1024x1024/1200x800/filters:focal(444x567:606x729)/cdn.vox-cdn.com/uploads/chorus_image/image/65813576/mobile_MarioKartTour_screen_01.0.png")) ;
        games.add(new Game("Jumanji: The video Game","$56","$56", 56,"https://i.ytimg.com/vi/uODKrZnGk7g/maxresdefault.jpg"));
        games.add(new Game("Minecraft","" ,"" ,35 , "https://upload.wikimedia.org/wikipedia/en/5/51/Minecraft_cover.png"));
        games.add(new Game("Fortnite", "","",55 ,"data:image/jpeg;base64,"));

        GamesRecViewAdapter adapter = new GamesRecViewAdapter(this) ;
        adapter.setGames(games) ; // set the arraylist of the adapter class to the arraylist we have just created.

        gamesRecView.setAdapter(adapter) ;
        gamesRecView.setLayoutManager(new GridLayoutManager(this,2));
    }

}